	  var update = utils.clone(this._update, { retainKeyOrder: true });
	  var ops = Object.keys(update);
	  var i = ops.length;
	  var ret = {};

	  while (i--) {
	    var op = ops[i];

	    if (this.options.overwrite) {
	      ret[op] = update[op];
	      continue;
	    }

	    if ('$' !== op[0]) {
	      // fix up $set sugar
	      if (!ret.$set) {
	        if (update.$set) {
	          ret.$set = update.$set;
	        } else {
	          ret.$set = {};
	        }
	      }
	      ret.$set[op] = update[op];
	      ops.splice(i, 1);
	      if (!~ops.indexOf('$set')) ops.push('$set');
	    } else if ('$set' === op) {
	      if (!ret.$set) {
	        ret[op] = update[op];
	      }
	    } else {
	      ret[op] = update[op];
	    }
	  }

	  this._compiledUpdate = ret;
	  return ret;
